	(11859,477,'_about_us_page_content_1_directors_buckets_2_position','field_533c6ddeeba9f'),
	(11860,477,'about_us_page_content_1_directors_buckets_2_bio','Michael Barber is a GE Officer and the VP of GE’s business strategy on global health for the corporation. The healthymagination charter is to improve the quality of care by 15% or more, to reduce the cost of procedures and processes through the appropriate use of GE technologies and services by 15% and to increase the access to essential technologies and services essential to health by 15%.'),
	(11861,477,'_about_us_page_content_1_directors_buckets_2_bio','field_533c6de5ebaa0'),
	(11862,477,'about_us_page_content_1_directors_buckets_3_name','Kevin Brown'),
