	(12369,479,'about_us_page_content_2_advisory_buckets_3_position','Medical Advisory Board Member'),
	(12370,479,'_about_us_page_content_2_advisory_buckets_3_position','field_533c6ffafd659'),
	(12371,479,'about_us_page_content_2_advisory_buckets_3_bio','Dr. Kane is a medical informatics specialist who co-created the San Francisco General Department of Medicine\'s Alternative Therapies Unit, one of the first programs of its kind in a major US medical center. She now consults at Healthline and teaches a doctor-patient communications course at Stanford Medical School.'),
	(12372,479,'_about_us_page_content_2_advisory_buckets_3_bio','field_533c6ffafd65a'),
