	(12377,479,'about_us_page_content_2_advisory_buckets_4_bio','Dr. Kizer is currently President, CEO and Chairman of Medsphere Systems Corporation, a leading provider of open source technology for the healthcare industry. He practiced emergency medicine and medical toxicology in both academic and private practice settings for 15 years.'),
	(12378,479,'_about_us_page_content_2_advisory_buckets_4_bio','field_533c6ffafd65a'),
	(12379,479,'about_us_page_content_2_advisory_buckets_5_name','James Norman, MD'),
