	(13708,482,'_about_us_page_content_1_directors_buckets_0_position','field_533c6ddeeba9f'),
	(13709,482,'about_us_page_content_1_directors_buckets_0_bio','Philip Dur has been involved in venture capital and private equity since 1995 and joined Investor Growth Capital’s technology investment team in 2004. He works out of IGC\'s Menlo Park office and focuses on investments in software and technology-enabled services businesses.'),
	(13710,482,'_about_us_page_content_1_directors_buckets_0_bio','field_533c6de5ebaa0'),
	(13711,482,'about_us_page_content_1_directors_buckets_1_name','Richard Harroch'),
