	(13749,482,'about_us_page_content_2_headline','Medical Advisory Board'),
	(13750,482,'_about_us_page_content_2_headline','field_533c6ffafd655'),
	(13751,482,'about_us_page_content_2_paragraph','The Healthline Medical Advisory Board is comprised of nationally and internationally recognized medical experts who serve to better the Healthline user experience by evaluating our website content, making recommendations on the vision and direction of the site, and providing input into Healthline\'s Medically Guided Search components, including its taxonomy, navigation and other features. The board also interfaces with customers and partners that use or offer the Healthline search experience.'),
	(13752,482,'_about_us_page_content_2_paragraph','field_533c6ffafd656'),
	(13753,482,'about_us_page_content_2_advisory_buckets_0_name','Paul Auerbach, MD, MS, FACEP'),
	(13754,482,'_about_us_page_content_2_advisory_buckets_0_name','field_533c6ffafd658'),
	(13755,482,'about_us_page_content_2_advisory_buckets_0_position','Chair, Medical Advisory Board'),
	(13756,482,'_about_us_page_content_2_advisory_buckets_0_position','field_533c6ffafd659'),
	(13757,482,'about_us_page_content_2_advisory_buckets_0_bio','Dr. Auerbach is the Redlich Family Professor of Surgery in the Division of Emergency Medicine at Stanford University. He is internationally recognized as a leading authority on emergency medicine, wilderness medicine and outdoor health and safety.'),
	(13758,482,'_about_us_page_content_2_advisory_buckets_0_bio','field_533c6ffafd65a'),
	(13759,482,'about_us_page_content_2_advisory_buckets_1_name','Colleen Conway-Welch, PhD, RN, FAAN'),
	(13760,482,'_about_us_page_content_2_advisory_buckets_1_name','field_533c6ffafd658'),
	(13761,482,'about_us_page_content_2_advisory_buckets_1_position','Medical Advisory Board Member'),
	(13762,482,'_about_us_page_content_2_advisory_buckets_1_position','field_533c6ffafd659'),
	(13763,482,'about_us_page_content_2_advisory_buckets_1_bio','Dr. Conway-Welch has served as Dean of Vanderbilt University\'s school of nursing since 1984. She is the founder and director of the International Nursing Coalition for Mass Casualty Education.'),
	(13764,482,'_about_us_page_content_2_advisory_buckets_1_bio','field_533c6ffafd65a'),
	(13765,482,'about_us_page_content_2_advisory_buckets_2_name','John Halamka, MD, MS'),
