	(13767,482,'about_us_page_content_2_advisory_buckets_2_position','Medical Advisory Board Member'),
	(13768,482,'_about_us_page_content_2_advisory_buckets_2_position','field_533c6ffafd659'),
	(13769,482,'about_us_page_content_2_advisory_buckets_2_bio','Dr. Halamka is Chief Information Officer of the CareGroup Health System, Chief Information Officer and Associate Dean for Educational Technology at Harvard Medical School, Chief Information Officer of the Harvard Clinical Research Institute, and a practicing emergency physician.'),
	(13770,482,'_about_us_page_content_2_advisory_buckets_2_bio','field_533c6ffafd65a'),
	(13771,482,'about_us_page_content_2_advisory_buckets_3_name','Beverley Kane, MD'),
	(13772,482,'_about_us_page_content_2_advisory_buckets_3_name','field_533c6ffafd658'),
	(13773,482,'about_us_page_content_2_advisory_buckets_3_position','Medical Advisory Board Member'),
	(13774,482,'_about_us_page_content_2_advisory_buckets_3_position','field_533c6ffafd659'),
	(13775,482,'about_us_page_content_2_advisory_buckets_3_bio','Dr. Kane is a medical informatics specialist who co-created the San Francisco General Department of Medicine\'s Alternative Therapies Unit, one of the first programs of its kind in a major US medical center. She now consults at Healthline and teaches a doctor-patient communications course at Stanford Medical School.'),
	(13776,482,'_about_us_page_content_2_advisory_buckets_3_bio','field_533c6ffafd65a'),
	(13777,482,'about_us_page_content_2_advisory_buckets_4_name','Kenneth W. Kizer, MD'),
	(13778,482,'_about_us_page_content_2_advisory_buckets_4_name','field_533c6ffafd658'),
	(13779,482,'about_us_page_content_2_advisory_buckets_4_position','Medical Advisory Board Member'),
	(13780,482,'_about_us_page_content_2_advisory_buckets_4_position','field_533c6ffafd659'),
	(13781,482,'about_us_page_content_2_advisory_buckets_4_bio','Dr. Kizer is currently President, CEO and Chairman of Medsphere Systems Corporation, a leading provider of open source technology for the healthcare industry. He practiced emergency medicine and medical toxicology in both academic and private practice settings for 15 years.'),
	(13782,482,'_about_us_page_content_2_advisory_buckets_4_bio','field_533c6ffafd65a'),
	(13783,482,'about_us_page_content_2_advisory_buckets_5_name','James Norman, MD'),
