	(14240,483,'_about_us_page_content_2_headline','field_533c6ffafd655'),
	(14241,483,'about_us_page_content_2_paragraph','The Healthline Medical Advisory Board is comprised of nationally and internationally recognized medical experts who serve to better the Healthline user experience by evaluating our website content, making recommendations on the vision and direction of the site, and providing input into Healthline\'s Medically Guided Search components, including its taxonomy, navigation and other features. The board also interfaces with customers and partners that use or offer the Healthline search experience.'),
	(14242,483,'_about_us_page_content_2_paragraph','field_533c6ffafd656'),
	(14243,483,'about_us_page_content_2_advisory_buckets_0_name','Paul Auerbach, MD, MS, FACEP'),
	(14244,483,'_about_us_page_content_2_advisory_buckets_0_name','field_533c6ffafd658'),
	(14245,483,'about_us_page_content_2_advisory_buckets_0_position','Chair, Medical Advisory Board'),
	(14246,483,'_about_us_page_content_2_advisory_buckets_0_position','field_533c6ffafd659'),
	(14247,483,'about_us_page_content_2_advisory_buckets_0_bio','Dr. Auerbach is the Redlich Family Professor of Surgery in the Division of Emergency Medicine at Stanford University. He is internationally recognized as a leading authority on emergency medicine, wilderness medicine and outdoor health and safety.'),
